/**
 * @title Login
 * @description A minimal authentication screen with email/password inputs
 * @categories authentication
 */

import type { ReactNode } from "react";
import { Text, View, type ViewProps } from "react-native";
import { cn } from "@/lib/utils";

export type LoginProps = {
  children: ReactNode;
};

const LoginRoot = ({ children, className, ...props }: LoginProps & ViewProps) => (
  <View {...props} className={cn("flex-1 gap-8 px-8", className)}>
    {children}
  </View>
);

const LoginHeader = ({ className, children, ...props }: ViewProps) => (
  <View className={cn("gap-2", className)} {...props}>
    {children}
  </View>
);

type LoginTextProps = {
  children: ReactNode;
} & Omit<ViewProps, "children">;

const LoginTitle = ({ className, children, ...props }: LoginTextProps) => (
  <Text className={cn("text-left font-bold text-3xl text-foreground", className)} {...props}>
    {children}
  </Text>
);

const LoginDescription = ({ className, children, ...props }: LoginTextProps) => (
  <Text className={cn("text-left text-muted", className)} {...props}>
    {children}
  </Text>
);

const LoginContent = ({ className, children, ...props }: ViewProps) => (
  <View className={cn("gap-4", className)} {...props}>
    {children}
  </View>
);

const LoginFooter = ({ className, children, ...props }: ViewProps) => (
  <View className={cn("gap-4", className)} {...props}>
    {children}
  </View>
);

export const Login = Object.assign(LoginRoot, {
  Header: LoginHeader,
  Title: LoginTitle,
  Description: LoginDescription,
  Content: LoginContent,
  Footer: LoginFooter,
});
